let i,transitionItemsDelay=2800,transitionItems=[];function startTransitionItems(){i=Math.floor(100*Math.random())%(transitionItems.length-1),show(transitionItems[i]),i++}function animateLoop(){setTimeout(()=>{showNextItem(i),i<transitionItems.length-1?i++:i=0,animateLoop(transitionItems)},transitionItemsDelay)}function showNextItem(t){let s=0==t?transitionItems[transitionItems.length-1]:transitionItems[t-1],i=transitionItems[t];hide(s),show(i)}function show(t){console.log("show",t);let s=$(t);s.hasClass("show")||s.addClass("show")}function hide(t){let s=$(t);s.hasClass("show")&&s.removeClass("show")}$(document).ready(()=>{transitionItems=$(".transitionItem"),startTransitionItems(),animateLoop()});